import React, { useState, useEffect } from "react";
import "./nav.css";
import { BrowserRouter, Routes, Route, NavLink } from "react-router-dom";
import Home from "./home";
import Contact from "./contact";
import About from "./about";
import LoadingAnimation from "./loadinganimation";
import { useLocation } from "react-router-dom";
import Blogs from "./blogs";
import Updates from "./updates";

function Navbar() {
  const [showMobileMenu, setShowMobileMenu] = useState(false);
  const location = useLocation();
  const shouldShrink = location.pathname !== "/";

  function handleHamburgerClick() {
    setShowMobileMenu(!showMobileMenu);
  }

  useEffect(() => {
    function handleScroll() {
      const navbar = document.querySelector(".navbar");
      if (navbar) {
        if (window.scrollY > 0) {
          navbar.classList.add("shrink");
        } else {
          navbar.classList.remove("shrink");
        }
      }
    }

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <nav className={`navbar ${shouldShrink ? "shrink" : ""}`}>
      <a href="#" className="logo">
        Team-14
      </a>
      <div className={`nav-links ${showMobileMenu ? "mobile-menu" : ""}`}>
        <ul className="nav-menu">
          <li className="active">
            <NavLink
              to="/"
              style={({ isActive }) => ({
                color: isActive ? "greenyellow" : "white",
              })}
            >
              Home
            </NavLink>
          </li>

          <li>
            <NavLink
              to="/blogs"
              style={({ isActive }) => ({
                color: isActive ? "greenyellow" : "white",
              })}
            >
              Blogs <i className="fab fa-blogger-b"></i>{" "}
            </NavLink>
          </li>

          <li>
            <NavLink
              to="/postBlogs"
              style={({ isActive }) => ({
                color: isActive ? "greenyellow" : "white",
              })}
            >
              Post blogs <i className="fa-message"> </i>
            </NavLink>
          </li>
          <li>
            <NavLink
              to="/updates" 
              style={({ isActive }) => ({
                color: isActive ? "greenyellow" : "white",
              })}
            >
              {" "}
              Updates <i className=""> </i>
            </NavLink>
          </li>
          <li>
            <NavLink
              to="/devteam"
              target="_blank"  style={({ isActive }) => ({
                color: isActive ? "greenyellow" : "white",
              })}
            >
              {" "}
              devteam
            </NavLink>
          </li>
          </ul>
      </div>
      <i className="menu-hamburger fa fa-bars" onClick={handleHamburgerClick}></i>
    </nav>
  );
}

function Header() {
  return <header className="header"></header>;
}

export default function App() {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsLoading(false); // simulate a 2-second loading time
    }, 2000);
    return () => clearTimeout(timer);
  }, []);

  return (
    <>
      {isLoading ? (
        <LoadingAnimation />
      ) : (
        <BrowserRouter>
          <Navbar />
          <Header />
          <Routes>
            <Route exact path="/" element={<Home />} />
            <Route exact path="/about" element={<About />} />
            <Route exact path="/postBlogs" element={<Contact />} />
            <Route exact path="/blogs" element={<Blogs />} />
            <Route exact path="/d" element={<Updates />} />
          </Routes>
        </BrowserRouter>
      )}
    </>
  );
}
